"use client";

import {FaEye, FaFingerprint, FaPencilAlt, FaPlus, FaSearch, FaTrash, FaUserCircle} from "react-icons/fa";
import {useEffect, useState} from "react";
import AddNewPasswordModal from "../components/CreateUpdatePasswordModal";
import ShowPasswordModal from "@/components/ShowPasswordModal";
import PinModal from "@/components/PinModal";
import AuthService from "@/services/AuthService";

export default function HomePage() {
    const [passwords, setPasswords] = useState([]);
    const [filteredPasswords, setFilteredPasswords] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const [showModal, setShowModal] = useState(false);
    const [editPassword, setEditPassword] = useState(null);
    const [viewPassword, setViewPassword] = useState(null);
    const [isAuthenticating, setIsAuthenticating] = useState(false);
    const [selectedPasswordForAuth, setSelectedPasswordForAuth] = useState(null);
    const [passwordToDelete, setPasswordToDelete] = useState(null);
    const [biometricAvailable, setBiometricAvailable] = useState(false);
    const [showPinSetup, setShowPinSetup] = useState(false);
    const [logs, setLogs] = useState([]);
    const [authAction, setAuthAction] = useState(null);

    const addLogs = (message) => {
        setLogs(prevLogs => [...prevLogs, `${message}\n`]);
    }

    // Load passwords from localStorage on mount
    useEffect(() => {
        loadPasswords();
    }, []);

    // Update filteredPasswords when passwords change
    useEffect(() => {
        setFilteredPasswords(passwords);
    }, [passwords]);

    // Save passwords to localStorage whenever passwords change
    useEffect(() => {
        if (typeof window !== "undefined") {
            localStorage.setItem("passwords", JSON.stringify(passwords));
            // Optional: console.log for debugging
            // console.log('Passwords saved to localStorage', passwords);
        }
    }, [passwords]);

    // Check for biometric availability on mount
    useEffect(() => {
        const checkBiometricAvailability = async () => {
            const available = await AuthService.isBiometricAvailable();
            addLogs(`isBiometricAvailable: ${available}`);
            setBiometricAvailable(available);

            // If no PIN is set, show PIN setup on first load
            if (!AuthService.hasPin()) {
                setShowPinSetup(true);
            }
        };

        checkBiometricAvailability();
    }, []);

    function loadPasswords() {
        const stored = typeof window !== "undefined" ? localStorage.getItem("passwords") : null;
        if (stored) {
            try {
                setPasswords(JSON.parse(stored));
            } catch {
                setPasswords([]);
            }
        }
    }

    function handleSearch(value) {
        setSearchTerm(value);
        if (!value.trim()) {
            setFilteredPasswords(passwords);
            return;
        }
        const filtered = passwords.filter(password =>
            password.title?.toLowerCase().includes(value.toLowerCase()) ||
            password.username?.toLowerCase().includes(value.toLowerCase()) ||
            password.url?.toLowerCase().includes(value.toLowerCase())
        );
        setFilteredPasswords(filtered);
    }

    function handleKeyDown(e) {
        const value = e.target.value;
        handleSearch(value);
    }

    function addNewPassword(newPassword, isEdit = false) {
        if (isEdit && newPassword.id) {
            setPasswords(prev =>
                prev.map(p => (p.id === newPassword.id ? newPassword : p))
            );
        } else {
            setPasswords(prev => [...prev, newPassword]);
        }
        setSearchTerm("");
        setShowModal(false);
        setEditPassword(null);
    }

    function onCloseNewPasswordModal() {
        setShowModal(false);
        setEditPassword(null);
    }

    async function validateDeviceAuthentication(callbackFunc, ...args) {
        // First check if we have a PIN set up
        const hasPin = AuthService.hasPin();
        addLogs(`Has PIN: ${hasPin}`);
        addLogs(`biometricAvailable: ${biometricAvailable}`);

        // If biometrics are available, try that first
        if (biometricAvailable) {
            setIsAuthenticating(true);
            try {
                const response = await AuthService.authenticateLocal();
                setLogs((prevLogs) => [...prevLogs, ...response.logs]);
                if (response.success) {
                    if (!callbackFunc) {
                        throw new Error("Callback function is required for biometric authentication");
                    }
                    callbackFunc(...args);
                    setIsAuthenticating(false);
                    return;
                }
            } catch (error) {
                console.error("Biometric authentication failed:", error);
            }

            // Biometric failed, fall back to PIN
            setIsAuthenticating(false);
        }

        // If we have a PIN, show PIN verification
        if (hasPin) {
            setIsAuthenticating(true);
        } else {
            // No PIN set up, show PIN setup
            setShowPinSetup(true);
        }
    }

    async function handleEditPassword(password) {
        setEditPassword(password);
        setAuthAction('edit'); // Set what action we're authenticating for
        await validateDeviceAuthentication(setShowModal, true);
    }

    async function handleViewPassword(password) {
        setSelectedPasswordForAuth(password);
        setAuthAction('view'); // Set action to view
        await validateDeviceAuthentication(setViewPassword, password);
    }

    async function handleDeletePassword(password) {
        setPasswordToDelete(password);
        setAuthAction('delete'); // Set action to delete
        await validateDeviceAuthentication(confirmDelete, password);
    }

    function confirmDelete(password) {
        const updatedPasswords = passwords.filter(p => p.id !== password.id);
        setPasswords(updatedPasswords);
        setPasswordToDelete(null);
    }

    function handlePinSuccess() {
        setIsAuthenticating(false);
        setShowPinSetup(false);

        // Handle different actions based on authAction
        if (authAction === 'view' && selectedPasswordForAuth) {
            setViewPassword(selectedPasswordForAuth);
            setSelectedPasswordForAuth(null);
        } else if (authAction === 'edit' && editPassword) {
            setShowModal(true); // Open the edit modal
        } else if (authAction === 'delete' && passwordToDelete) {
            confirmDelete(passwordToDelete);
        }

        setAuthAction(null); // Reset action
    }

    function handlePinCancel() {
        setIsAuthenticating(false);
        setShowPinSetup(false);
        setSelectedPasswordForAuth(null);
        setAuthAction(null); // Reset action

        // Clear edit state on cancel
        if (authAction === 'edit') {
            setEditPassword(null);
        }

        // Clear delete state on cancel
        if (authAction === 'delete') {
            setPasswordToDelete(null);
        }
    }

    function closeViewModal() {
        setViewPassword(null);
    }

    function onClickOutsideLoader(e) {
        console.log(e, 'clicked outside loader');
        e.stopPropagation();
        setIsAuthenticating(false);
    }

    // Make sure PIN setup works first time
    useEffect(() => {
        // Check if we should show PIN setup on first visit
        if (typeof window !== "undefined" && !localStorage.getItem('securepass_pin_setup_shown')) {
            setShowPinSetup(true);
            localStorage.setItem('securepass_pin_setup_shown', 'true');
        }
    }, []);

    return (
        <div className="min-h-screen pb-20">
            <div className="relative w-full max-w-md mx-auto mt-4 mb-8">
                <input
                    type="text"
                    placeholder="Search passwords..."
                    defaultValue={searchTerm}
                    onKeyUp={handleKeyDown}
                    className="w-full pl-12 pr-4 py-3 rounded-xl text-black bg-white/80 backdrop-blur-sm focus:bg-white
                    shadow-lg focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all border border-gray-200"
                />
                <FaSearch className="absolute left-4 top-1/2 -translate-y-1/2 text-gray-500"/>
            </div>

            {/* Add Button */}
            <button
                onClick={() => setShowModal(true)}
                className="fixed bottom-8 right-8 bg-gradient-to-r from-blue-500 to-indigo-600 hover:from-blue-600
                hover:to-indigo-700 text-white rounded-full p-4 shadow-xl flex items-center justify-center z-30
                transition-all duration-300 transform hover:scale-105"
                aria-label="Add new password"
            >
                <FaPlus className="text-2xl"/>
            </button>

            {/* Add/Edit Modal */}
            {showModal && (
                <AddNewPasswordModal
                    addNewPassword={addNewPassword}
                    onCloseModal={onCloseNewPasswordModal}
                    editPassword={editPassword}
                />
            )}

            {/* PIN Setup Modal */}
            {showPinSetup && (
                <PinModal
                    onSuccess={handlePinSuccess}
                    onCancel={handlePinCancel}
                    isSetup={true}
                />
            )}

            {/* PIN Verification Modal */}
            {isAuthenticating && !biometricAvailable && (
                <PinModal
                    onSuccess={handlePinSuccess}
                    onCancel={handlePinCancel}
                />
            )}

            {/* Biometric Authentication Indicator */}
            {isAuthenticating && biometricAvailable && (
                <div
                    className="fixed inset-0 z-50 flex items-center justify-center bg-black/50 backdrop-blur-sm animate-fadeIn"
                    onClick={onClickOutsideLoader}>
                    <div className="bg-white rounded-2xl shadow-2xl p-8 text-center transform animate-scaleIn">
                        <FaFingerprint className="text-6xl text-blue-600 mx-auto mb-4 animate-pulse"/>
                        <h2 className="text-xl font-bold bg-gradient-to-r from-blue-600 to-indigo-600 bg-clip-text text-transparent">
                            Verifying Identity
                        </h2>
                        <p className="text-gray-500 mt-2">
                            Use your device&#39;s security method
                        </p>
                    </div>
                </div>
            )}

            {/* View Password Modal */}
            {viewPassword && (
                <ShowPasswordModal closeViewModal={closeViewModal} viewPassword={viewPassword}/>
            )}

            <h2 className="text-2xl font-bold mb-6 text-white tracking-tight flex items-center">
                <span className="bg-gradient-to-r from-blue-400 to-indigo-500 bg-clip-text text-transparent">
                    Saved Passwords
                </span>
            </h2>

            {filteredPasswords.length === 0 ? (
                <div className="bg-white/80 backdrop-blur-sm p-8 rounded-xl shadow-lg text-center">
                    <p className="text-gray-600">No passwords found. Add your first password!</p>
                </div>
            ) : (
                <ul className="space-y-4">
                    {filteredPasswords.map((item) => (
                        <li
                            key={item.id}
                            className="bg-white/90 backdrop-blur-sm p-5 rounded-xl shadow-lg flex items-center
                            justify-between hover:shadow-xl transition-all duration-300 border border-gray-100
                            hover:border-blue-200 hover:bg-white"
                        >
                            <div className="flex items-center gap-4">
                                <div
                                    className="bg-gradient-to-br from-blue-500 to-indigo-600 p-3 rounded-lg text-white">
                                    <FaUserCircle className="text-2xl"/>
                                </div>
                                <div>
                                    <p className="font-semibold text-lg text-gray-800">{item.title}</p>
                                    <p className="text-gray-500 text-sm">{item.username}</p>
                                </div>
                            </div>
                            <div className="flex gap-2">
                                <button
                                    className="bg-gray-800 hover:bg-gray-900 px-4 py-2 text-white rounded-lg flex
                                    items-center gap-2 shadow transition-all duration-200 hover:shadow-md"
                                    onClick={() => handleViewPassword(item)}
                                >
                                    <FaEye/> <span className="hidden sm:inline">View</span>
                                </button>
                                <button
                                    className="bg-gradient-to-r from-blue-500 to-blue-600 hover:from-blue-600 hover:to-blue-700
                                    px-4 py-2 text-white rounded-lg flex items-center gap-2 shadow transition-all duration-200
                                    hover:shadow-md"
                                    onClick={() => handleEditPassword(item)}
                                    aria-label="Edit"
                                >
                                    <FaPencilAlt/> <span className="hidden sm:inline">Edit</span>
                                </button>
                                <button
                                    className="bg-gradient-to-r from-red-500 to-red-600 hover:from-red-600 hover:to-red-700
                                    px-4 py-2 text-white rounded-lg flex items-center gap-2 shadow transition-all duration-200
                                    hover:shadow-md"
                                    onClick={() => handleDeletePassword(item)}
                                    aria-label="Delete">
                                    <FaTrash/>
                                    <span className="hidden sm:inline">Delete</span>
                                </button>
                            </div>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
}

"use client";

import {FaEye, FaFingerprint, FaPlus, FaSearch, FaUserCircle, FaPencilAlt, FaTrash, FaKey, FaLock} from "react-icons/fa";
import {useEffect, useState} from "react";
import AddNewPasswordModal from "../components/CreateUpdatePasswordModal";
import ShowPasswordModal from "@/components/ShowPasswordModal";
import PinModal from "@/components/PinModal";
import AuthService from "@/services/AuthService";

export default function HomePage() {
    const [passwords, setPasswords] = useState([]);
    const [filteredPasswords, setFilteredPasswords] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const [showModal, setShowModal] = useState(false);
    const [editPassword, setEditPassword] = useState(null);
    const [viewPassword, setViewPassword] = useState(null);
    const [isAuthenticating, setIsAuthenticating] = useState(false);
    const [selectedPasswordForAuth, setSelectedPasswordForAuth] = useState(null);
    const [passwordToDelete, setPasswordToDelete] = useState(null);
    const [biometricAvailable, setBiometricAvailable] = useState(false);
    const [showPinSetup, setShowPinSetup] = useState(false);
    const [logs, setLogs] = useState([]);
    const [authAction, setAuthAction] = useState(null);

    const addLogs = (message) => {
        setLogs(prevLogs => [...prevLogs, `${message}\n`]);
    }

    // Load passwords from localStorage on mount
    useEffect(() => {
        loadPasswords();
    }, []);

    // Update filteredPasswords when passwords change
    useEffect(() => {
        setFilteredPasswords(passwords);
    }, [passwords]);

    // Save passwords to localStorage whenever passwords change
    useEffect(() => {
        if (typeof window !== "undefined") {
            localStorage.setItem("passwords", JSON.stringify(passwords));
            // Optional: console.log for debugging
            // console.log('Passwords saved to localStorage', passwords);
        }
    }, [passwords]);

    // Check for biometric availability on mount
    useEffect(() => {
        const checkBiometricAvailability = async () => {
            const available = await AuthService.isBiometricAvailable();
            addLogs(`isBiometricAvailable: ${available}`);
            setBiometricAvailable(available);

            // If no PIN is set, show PIN setup on first load
            if (!AuthService.hasPin()) {
                setShowPinSetup(true);
            }
        };

        checkBiometricAvailability();
    }, []);

    function loadPasswords() {
        const stored = typeof window !== "undefined" ? localStorage.getItem("passwords") : null;
        if (stored) {
            try {
                setPasswords(JSON.parse(stored));
            } catch {
                setPasswords([]);
            }
        }
    }

    function handleSearch(value) {
        setSearchTerm(value);
        if (!value.trim()) {
            setFilteredPasswords(passwords);
            return;
        }
        const filtered = passwords.filter(password =>
            password.title?.toLowerCase().includes(value.toLowerCase()) ||
            password.username?.toLowerCase().includes(value.toLowerCase()) ||
            password.url?.toLowerCase().includes(value.toLowerCase())
        );
        setFilteredPasswords(filtered);
    }

    function handleKeyDown(e) {
        const value = e.target.value;
        handleSearch(value);
    }

    function addNewPassword(newPassword, isEdit = false) {
        if (isEdit && newPassword.id) {
            setPasswords(prev =>
                prev.map(p => (p.id === newPassword.id ? newPassword : p))
            );
        } else {
            setPasswords(prev => [...prev, newPassword]);
        }
        setSearchTerm("");
        setShowModal(false);
        setEditPassword(null);
    }

    function onCloseNewPasswordModal() {
        setShowModal(false);
        setEditPassword(null);
    }

    async function validateDeviceAuthentication(callbackFunc, ...args) {
        // First check if we have a PIN set up
        const hasPin = AuthService.hasPin();
        addLogs(`Has PIN: ${hasPin}`);
        addLogs(`biometricAvailable: ${biometricAvailable}`);

        // If biometrics are available, try that first
        if (biometricAvailable) {
            setIsAuthenticating(true);
            try {
                const response = await AuthService.authenticateLocal();
                setLogs((prevLogs) => [...prevLogs, ...response.logs]);
                if (response.success) {
                    if (!callbackFunc) {
                        throw new Error("Callback function is required for biometric authentication");
                    }
                    callbackFunc(...args);
                    setIsAuthenticating(false);
                    return;
                }
            } catch (error) {
                console.error("Biometric authentication failed:", error);
            }

            // Biometric failed, fall back to PIN
            setIsAuthenticating(false);
        }

        // If we have a PIN, show PIN verification
        if (hasPin) {
            setIsAuthenticating(true);
        } else {
            // No PIN set up, show PIN setup
            setShowPinSetup(true);
        }
    }

    async function handleEditPassword(password) {
        setEditPassword(password);
        setAuthAction('edit'); // Set what action we're authenticating for
        await validateDeviceAuthentication(setShowModal, true);
    }

    async function handleViewPassword(password) {
        setSelectedPasswordForAuth(password);
        setAuthAction('view'); // Set action to view
        await validateDeviceAuthentication(setViewPassword, password);
    }

    async function handleDeletePassword(password) {
        setPasswordToDelete(password);
        setAuthAction('delete'); // Set action to delete
        await validateDeviceAuthentication(confirmDelete, password);
    }

    function confirmDelete(password) {
        const updatedPasswords = passwords.filter(p => p.id !== password.id);
        setPasswords(updatedPasswords);
        setPasswordToDelete(null);
    }

    function handlePinSuccess() {
        setIsAuthenticating(false);
        setShowPinSetup(false);

        // Handle different actions based on authAction
        if (authAction === 'view' && selectedPasswordForAuth) {
            setViewPassword(selectedPasswordForAuth);
            setSelectedPasswordForAuth(null);
        } else if (authAction === 'edit' && editPassword) {
            setShowModal(true); // Open the edit modal
        } else if (authAction === 'delete' && passwordToDelete) {
            confirmDelete(passwordToDelete);
        }

        setAuthAction(null); // Reset action
    }

    function handlePinCancel() {
        setIsAuthenticating(false);
        setShowPinSetup(false);
        setSelectedPasswordForAuth(null);
        setAuthAction(null); // Reset action

        // Clear edit state on cancel
        if (authAction === 'edit') {
            setEditPassword(null);
        }

        // Clear delete state on cancel
        if (authAction === 'delete') {
            setPasswordToDelete(null);
        }
    }

    function closeViewModal() {
        setViewPassword(null);
    }

    function onClickOutsideLoader(e) {
        console.log(e, 'clicked outside loader');
        e.stopPropagation();
        setIsAuthenticating(false);
    }

    // Make sure PIN setup works first time
    useEffect(() => {
        // Check if we should show PIN setup on first visit
        if (typeof window !== "undefined" && !localStorage.getItem('securepass_pin_setup_shown')) {
            setShowPinSetup(true);
            localStorage.setItem('securepass_pin_setup_shown', 'true');
        }
    }, []);

    return (
        <div className="min-h-screen pb-20 px-4 max-w-2xl mx-auto pt-20">
            {/* Search Bar */}
            <div className="relative w-full mx-auto mb-8">
                <input
                    type="text"
                    placeholder="Search passwords..."
                    defaultValue={searchTerm}
                    onKeyUp={handleKeyDown}
                    className="w-full pl-12 pr-4 py-3.5 rounded-xl text-gray-800 glass focus:outline-none 
                    focus:ring-2 focus:ring-indigo-400 transition-all duration-300 shadow-sm"
                />
                <FaSearch className="absolute left-4 top-1/2 -translate-y-1/2 text-gray-500"/>
            </div>

            {/* Add Button */}
            <button
                onClick={() => setShowModal(true)}
                className="fixed bottom-6 right-6 bg-white p-3.5 rounded-full shadow-lg hover:shadow-xl
                text-indigo-600 z-30 transition-all duration-300 transform hover:scale-110 border border-indigo-100"
                aria-label="Add new password"
            >
                <FaPlus className="text-xl"/>
            </button>

            {/* Section Title */}
            <div className="flex items-center space-x-3 mb-6">
                <div className="bg-indigo-500 p-2 rounded-lg text-white shadow-md">
                    <FaKey className="text-xl"/>
                </div>
                <h2 className="text-xl font-bold text-gray-800">
                    Your Passwords
                </h2>
            </div>

            {/* Password List */}
            {filteredPasswords.length === 0 ? (
                <div className="glass p-8 rounded-xl text-center animate-fadeIn">
                    <div className="w-16 h-16 rounded-full bg-indigo-100 text-indigo-500 flex items-center justify-center mx-auto mb-4">
                        <FaLock className="text-2xl"/>
                    </div>
                    <p className="text-gray-600 mb-2">No passwords found</p>
                    <p className="text-gray-500 text-sm">Add your first password using the + button</p>
                </div>
            ) : (
                <ul className="space-y-3">
                    {filteredPasswords.map((item) => (
                        <li
                            key={item.id}
                            className="glass p-4 rounded-xl flex items-center justify-between hover:shadow-md 
                            transition-all duration-300 border-l-4 border-indigo-500 animate-slideUp"
                        >
                            <div className="flex items-center gap-3">
                                <div className="bg-indigo-100 p-2.5 rounded-lg text-indigo-600">
                                    <FaUserCircle className="text-xl"/>
                                </div>
                                <div>
                                    <p className="font-medium text-gray-800">{item.title}</p>
                                    <p className="text-gray-500 text-sm">{item.username}</p>
                                </div>
                            </div>
                            <div className="flex gap-2">
                                <button
                                    onClick={() => handleViewPassword(item)}
                                    className="p-2 text-gray-600 hover:text-indigo-700 hover:bg-indigo-50 rounded-lg 
                                    transition-colors duration-200"
                                    aria-label="View"
                                >
                                    <FaEye/>
                                </button>
                                <button
                                    onClick={() => handleEditPassword(item)}
                                    className="p-2 text-gray-600 hover:text-indigo-700 hover:bg-indigo-50 rounded-lg 
                                    transition-colors duration-200"
                                    aria-label="Edit"
                                >
                                    <FaPencilAlt/>
                                </button>
                                <button
                                    onClick={() => handleDeletePassword(item)}
                                    className="p-2 text-gray-600 hover:text-red-600 hover:bg-red-50 rounded-lg 
                                    transition-colors duration-200"
                                    aria-label="Delete"
                                >
                                    <FaTrash/>
                                </button>
                            </div>
                        </li>
                    ))}
                </ul>
            )}

            {/* Biometric Authentication Indicator */}
            {isAuthenticating && biometricAvailable && (
                <div className="fixed inset-0 z-50 flex items-center justify-center bg-gray-900/40 backdrop-blur-sm animate-fadeIn" 
                     onClick={onClickOutsideLoader}>
                    <div className="bg-white rounded-2xl shadow-xl p-8 text-center transform animate-scaleIn max-w-xs w-full mx-4">
                        <FaFingerprint className="text-6xl text-indigo-500 mx-auto mb-6 animate-pulse"/>
                        <h2 className="text-xl font-semibold text-gray-800 mb-2">
                            Verifying Identity
                        </h2>
                        <p className="text-gray-500">
                            Use your device&#39;s security method
                        </p>
                    </div>
                </div>
            )}

            {/* Add/Edit Modal */}
            {showModal && (
                <AddNewPasswordModal
                    addNewPassword={addNewPassword}
                    onCloseModal={onCloseNewPasswordModal}
                    editPassword={editPassword}
                />
            )}

            {/* PIN Setup Modal */}
            {showPinSetup && (
                <PinModal
                    onSuccess={handlePinSuccess}
                    onCancel={handlePinCancel}
                    isSetup={true}
                />
            )}

            {/* PIN Verification Modal */}
            {isAuthenticating && !biometricAvailable && (
                <PinModal
                    onSuccess={handlePinSuccess}
                    onCancel={handlePinCancel}
                />
            )}

            {/* View Password Modal */}
            {viewPassword && (
                <ShowPasswordModal closeViewModal={closeViewModal} viewPassword={viewPassword}/>
            )}
        </div>
    );
}

import {FaPlus, FaTimes, FaSave} from "react-icons/fa";
import {useState, useEffect} from "react";

export default function AddPasswordPage({addNewPassword, onCloseModal, editPassword}) {
    const [payload, setPayload] = useState({
        title: "",
        username: "",
        password: "",
        url: "",
    });
    const [msgConfig, setMsgConfig] = useState({
        msg: '',
        show: false,
        type: 'error',
    });

    useEffect(() => {
        if (editPassword) {
            setPayload({
                title: editPassword.title || "",
                username: editPassword.username || "",
                password: editPassword.password || "",
                url: editPassword.url || "",
                id: editPassword.id,
            });
        } else {
            setPayload({
                title: "",
                username: "",
                password: "",
                url: "",
            });
        }
    }, [editPassword]);

    const closeModal = () => onCloseModal();

    const showError = (message) => {
        setMsgConfig({
            msg: message,
            show: true,
            type: 'error',
        })
    }

    const handleAddPassword = (e) => {
        e.preventDefault();
        if (!payload || !payload.title.trim() || !payload.username.trim() || !payload.password.trim()) {
            return showError('Please fill in all fields');
        }
        addNewPassword(
            {
                id: payload.id || Date.now(),
                url: payload.url || "",
                title: payload.title,
                username: payload.username,
                password: payload.password,
            },
            !!editPassword
        );
        closeModal();
    };

    const updatePayload = (e) => {
        setPayload((prev) => ({
            ...prev,
            [e.target.name]: e.target.value,
        }));
    }

    return (
        <>
            <div className="fixed inset-0 z-40 flex items-center justify-center bg-black/50 backdrop-blur-sm animate-fadeIn">
                <div className="bg-white rounded-2xl shadow-2xl p-8 w-full max-w-sm relative border border-gray-100 transform animate-scaleIn">
                    <button
                        onClick={closeModal}
                        className="absolute top-4 right-4 text-gray-400 hover:text-gray-700 transition-colors duration-200"
                        aria-label="Close"
                    >
                        <FaTimes className="text-xl"/>
                    </button>
                    <h2 className="text-2xl font-bold mb-6 bg-gradient-to-r from-blue-600 to-indigo-600 bg-clip-text text-transparent">
                        {editPassword ? "Edit Password" : "Add New Password"}
                    </h2>
                    <form className="space-y-5" onSubmit={handleAddPassword}>
                        <div className="group">
                            <label className="block text-sm font-medium text-gray-700 mb-1 ml-1">Title</label>
                            <input
                                type="text"
                                placeholder="Title"
                                name="title"
                                value={payload.title}
                                onChange={updatePayload}
                                className="w-full px-4 py-3 rounded-xl border border-gray-300 focus:border-blue-500
                                focus:ring-2 focus:ring-blue-200 transition-all duration-200 bg-white/90"
                                required
                            />
                        </div>
                        <div className="group">
                            <label className="block text-sm font-medium text-gray-700 mb-1 ml-1">Username</label>
                            <input
                                type="text"
                                placeholder="Username"
                                name="username"
                                value={payload.username}
                                onChange={updatePayload}
                                className="w-full px-4 py-3 rounded-xl border border-gray-300 focus:border-blue-500
                                focus:ring-2 focus:ring-blue-200 transition-all duration-200 bg-white/90"
                                required
                            />
                        </div>
                        <div className="group">
                            <label className="block text-sm font-medium text-gray-700 mb-1 ml-1">Password</label>
                            <input
                                type="password"
                                placeholder="Password"
                                name="password"
                                value={payload.password}
                                onChange={updatePayload}
                                className="w-full px-4 py-3 rounded-xl border border-gray-300 focus:border-blue-500
                                focus:ring-2 focus:ring-blue-200 transition-all duration-200 bg-white/90"
                                required
                            />
                        </div>
                        <button
                            type="submit"
                            className="bg-gradient-to-r from-blue-500 to-indigo-600 hover:from-blue-600 hover:to-indigo-700
                            px-6 py-3 text-white rounded-xl flex items-center gap-2 font-semibold shadow-lg transition-all
                            duration-300 w-full justify-center mt-4 hover:shadow-xl transform hover:scale-[1.02]"
                        >
                            {editPassword ? <FaSave/> : <FaPlus/>} {editPassword ? "Update Password" : "Add Password"}
                        </button>
                        {msgConfig.show && (
                            <div
                                className={`mt-4 p-3 rounded-xl ${msgConfig.type === 'error' ? 'bg-red-500 text-white' : 'bg-green-500 text-white'} 
                                animate-fadeIn shadow-md`}>
                                {msgConfig.msg}
                            </div>
                        )}
                    </form>
                </div>
            </div>
        </>
    );
}

import {FaPlus, FaTimes, FaSave, FaKey} from "react-icons/fa";
import {useState, useEffect} from "react";

export default function AddPasswordPage({addNewPassword, onCloseModal, editPassword}) {
    const [payload, setPayload] = useState({
        title: "",
        username: "",
        password: "",
        url: "",
    });
    const [msgConfig, setMsgConfig] = useState({
        msg: '',
        show: false,
        type: 'error',
    });

    useEffect(() => {
        if (editPassword) {
            setPayload({
                title: editPassword.title || "",
                username: editPassword.username || "",
                password: editPassword.password || "",
                url: editPassword.url || "",
                id: editPassword.id,
            });
        } else {
            setPayload({
                title: "",
                username: "",
                password: "",
                url: "",
            });
        }
    }, [editPassword]);

    const closeModal = () => onCloseModal();

    const showError = (message) => {
        setMsgConfig({
            msg: message,
            show: true,
            type: 'error',
        })
    }

    const handleAddPassword = (e) => {
        e.preventDefault();
        if (!payload || !payload.title.trim() || !payload.username.trim() || !payload.password.trim()) {
            return showError('Please fill in all fields');
        }
        addNewPassword(
            {
                id: payload.id || Date.now(),
                url: payload.url || "",
                title: payload.title,
                username: payload.username,
                password: payload.password,
            },
            !!editPassword
        );
        closeModal();
    };

    const updatePayload = (e) => {
        setPayload((prev) => ({
            ...prev,
            [e.target.name]: e.target.value,
        }));
    }

    return (
        <div className="fixed inset-0 z-40 flex items-center justify-center bg-gray-900/40 backdrop-blur-sm animate-fadeIn">
            <div className="bg-white rounded-2xl shadow-xl p-6 w-full max-w-md mx-4 relative animate-scaleIn">
                <button
                    onClick={closeModal}
                    className="absolute top-4 right-4 text-gray-400 hover:text-gray-600 p-1.5 rounded-full hover:bg-gray-100 transition-colors"
                    aria-label="Close"
                >
                    <FaTimes/>
                </button>

                <div className="flex items-center gap-3 mb-6">
                    <div className="bg-indigo-100 p-2.5 rounded-lg text-indigo-600">
                        <FaKey className="text-xl"/>
                    </div>
                    <h2 className="text-xl font-semibold text-gray-800">
                        {editPassword ? "Edit Password" : "Add New Password"}
                    </h2>
                </div>

                <form className="space-y-4" onSubmit={handleAddPassword}>
                    <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1 ml-1">Title</label>
                        <input
                            type="text"
                            placeholder="e.g. Gmail, Twitter, Bank"
                            name="title"
                            value={payload.title}
                            onChange={updatePayload}
                            className="w-full px-4 py-3 rounded-xl border border-gray-200 focus:border-indigo-500 
                            focus:ring-2 focus:ring-indigo-200 transition-all duration-200"
                            required
                        />
                    </div>

                    <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1 ml-1">Username</label>
                        <input
                            type="text"
                            placeholder="your.email@example.com"
                            name="username"
                            value={payload.username}
                            onChange={updatePayload}
                            className="w-full px-4 py-3 rounded-xl border border-gray-200 focus:border-indigo-500 
                            focus:ring-2 focus:ring-indigo-200 transition-all duration-200"
                            required
                        />
                    </div>

                    <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1 ml-1">Password</label>
                        <input
                            type="password"
                            placeholder="Enter password"
                            name="password"
                            value={payload.password}
                            onChange={updatePayload}
                            className="w-full px-4 py-3 rounded-xl border border-gray-200 focus:border-indigo-500 
                            focus:ring-2 focus:ring-indigo-200 transition-all duration-200"
                            required
                        />
                    </div>

                    <button
                        type="submit"
                        className="w-full mt-4 bg-indigo-600 hover:bg-indigo-700 text-white py-3 px-4 rounded-xl 
                        flex items-center justify-center gap-2 transition-all duration-300 shadow-md hover:shadow-lg"
                    >
                        {editPassword ? <FaSave/> : <FaPlus/>} 
                        {editPassword ? "Update Password" : "Add Password"}
                    </button>

                    {msgConfig.show && (
                        <div
                            className={`p-3 rounded-xl ${msgConfig.type === 'error' ? 'bg-red-100 text-red-800 border border-red-200' : 
                            'bg-green-100 text-green-800 border border-green-200'} animate-fadeIn`}>
                            {msgConfig.msg}
                        </div>
                    )}
                </form>
            </div>
        </div>
    );
}

"use client";

import Link from "next/link";
import { usePathname } from "next/navigation";
import { useEffect, useState } from "react";
import { FaHome, FaLock, FaDownload, FaKey } from "react-icons/fa";

export default function NavBar({ onSearch }) {
  const pathname = usePathname();
  const [deferredPrompt, setDeferredPrompt] = useState(null);
  const [showInstall, setShowInstall] = useState(false);

  useEffect(() => {
    // Hide install button if already running as standalone
    const isStandalone =
      typeof window !== "undefined" &&
      (window.matchMedia("(display-mode: standalone)").matches ||
        window.navigator.standalone === true);

    if (isStandalone) {
      setShowInstall(false);
      return;
    }

    const handler = (e) => {
      e.preventDefault();
      setDeferredPrompt(e);
      setShowInstall(true);
    };
    window.addEventListener("beforeinstallprompt", handler);

    // If the event never fires, hide the button
    return () => window.removeEventListener("beforeinstallprompt", handler);
  }, []);

  const handleInstall = async () => {
    if (deferredPrompt) {
      deferredPrompt.prompt();
      const { outcome } = await deferredPrompt.userChoice;
      if (outcome === "accepted") {
        setShowInstall(false);
      }
      setDeferredPrompt(null);
    }
  };

  const linkClass = (path) =>
    `flex items-center gap-2 px-4 py-2.5 rounded-xl transition-all duration-200 font-medium ${
      pathname === path
        ? "bg-indigo-600 text-white shadow-md"
        : "text-gray-700 hover:bg-gray-100"
    }`;

  return (
    <nav className="fixed top-0 left-0 right-0 z-20 bg-white shadow-sm border-b border-gray-100">
      <div className="max-w-screen-lg mx-auto px-4">
        <div className="flex items-center justify-between h-16">
          <div className="flex items-center gap-2 text-indigo-600 font-semibold text-lg">
            <FaKey className="text-xl" />
            <span>SecurePass</span>
          </div>

          <div className="flex items-center gap-2">
            <Link href="/" className={linkClass("/")}>
              <FaHome className="text-lg" />{" "}
              <span className="hidden sm:inline">Home</span>
            </Link>

            <Link href="/unlock" className={linkClass("/unlock")}>
              <FaLock className="text-lg" />{" "}
              <span className="hidden sm:inline">Unlock</span>
            </Link>

            {showInstall && (
              <button
                onClick={handleInstall}
                className="flex items-center gap-2 px-4 py-2.5 rounded-xl bg-green-600 text-white
                                font-medium shadow-sm hover:shadow-md transition-all duration-200 hover:bg-green-700"
              >
                <FaDownload />{" "}
                <span className="hidden sm:inline">Install App</span>
              </button>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
}

"use client";

import Link from "next/link";
import { usePathname } from "next/navigation";
import { useEffect, useState } from "react";
import { FaHome, FaLock, FaDownload, FaKey, FaSun, FaMoon } from "react-icons/fa";
import { useTheme } from "@/context/ThemeContext";

export default function NavBar({ onSearch }) {
  const pathname = usePathname();
  const [deferredPrompt, setDeferredPrompt] = useState(null);
  const [showInstall, setShowInstall] = useState(false);
  const { theme, toggleTheme } = useTheme();

  useEffect(() => {
    // Hide install button if already running as standalone
    const isStandalone =
      typeof window !== "undefined" &&
      (window.matchMedia("(display-mode: standalone)").matches ||
        window.navigator.standalone === true);

    if (isStandalone) {
      setShowInstall(false);
      return;
    }

    const handler = (e) => {
      e.preventDefault();
      setDeferredPrompt(e);
      setShowInstall(true);
    };
    window.addEventListener("beforeinstallprompt", handler);

    // If the event never fires, hide the button
    return () => window.removeEventListener("beforeinstallprompt", handler);
  }, []);

  const handleInstall = async () => {
    if (deferredPrompt) {
      deferredPrompt.prompt();
      const { outcome } = await deferredPrompt.userChoice;
      if (outcome === "accepted") {
        setShowInstall(false);
      }
      setDeferredPrompt(null);
    }
  };

  const linkClass = (path) =>
    `flex items-center gap-2 px-4 py-2.5 rounded-xl transition-all duration-200 font-medium ${
      pathname === path
        ? "bg-indigo-600 text-white shadow-md"
        : "text-gray-700 hover:bg-gray-100 dark:text-gray-300 dark:hover:bg-gray-700"
    }`;

  return (
    <nav className="fixed top-0 left-0 right-0 z-20 bg-white dark:bg-gray-800 shadow-sm border-b border-gray-100 dark:border-gray-700 transition-colors duration-300">
      <div className="max-w-screen-lg mx-auto px-4">
        <div className="flex items-center justify-between h-16">
          <div className="flex items-center gap-2 text-indigo-600 dark:text-indigo-400 font-semibold text-lg">
            <FaKey className="text-xl" />
            <span>SecurePass</span>
          </div>

          <div className="flex items-center gap-2">
            <Link href="/" className={linkClass("/")}>
              <FaHome className="text-lg" />{" "}
              <span className="hidden sm:inline">Home</span>
            </Link>

            <Link href="/unlock" className={linkClass("/unlock")}>
              <FaLock className="text-lg" />{" "}
              <span className="hidden sm:inline">Unlock</span>
            </Link>

            <button
              onClick={toggleTheme}
              className="p-2.5 text-gray-600 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-xl transition-colors"
              aria-label={
                theme === "dark"
                  ? "Switch to light mode"
                  : "Switch to dark mode"
              }
            >
              {theme === "dark" ? (
                <FaSun className="text-amber-300" />
              ) : (
                <FaMoon />
              )}
            </button>

            {showInstall && (
              <button
                onClick={handleInstall}
                className="flex items-center gap-2 px-4 py-2.5 rounded-xl bg-green-600 text-white
                font-medium shadow-sm hover:shadow-md transition-all duration-200 hover:bg-green-700"
              >
                <FaDownload />{" "}
                <span className="hidden sm:inline">Install App</span>
              </button>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
}

import { useState, useEffect } from "react";
import { FaTimes, FaLock, FaShieldAlt, FaCheck } from "react-icons/fa";
import AuthService from "../services/AuthService";

export default function PinModal({ onSuccess, onCancel, isSetup = false }) {
  const [pin, setPin] = useState("");
  const [confirmPin, setConfirmPin] = useState("");
  const [error, setError] = useState("");
  const [showConfirm, setShowConfirm] = useState(false);

  const handlePinChange = (e) => {
    const value = e.target.value;
    // Only allow numbers
    if (/^\d*$/.test(value) && value.length <= 6) {
      setPin(value);
      setError("");
    }
  };

  const handleConfirmPinChange = (e) => {
    const value = e.target.value;
    if (/^\d*$/.test(value) && value.length <= 6) {
      setConfirmPin(value);
      setError("");
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (isSetup) {
      if (!showConfirm) {
        if (pin.length < 4) {
          setError("PIN must be at least 4 digits");
          return;
        }
        setShowConfirm(true);
        return;
      } else {
        if (pin !== confirmPin) {
          setError("PINs don't match");
          return;
        }

        AuthService.savePin(pin);
        onSuccess();
      }
    } else {
      // Verification mode
      if (AuthService.verifyPin(pin)) {
        onSuccess();
      } else {
        setError("Incorrect PIN");
      }
    }
  };

  // Focus the input when modal opens
  useEffect(() => {
    // Small delay to ensure DOM is ready
    const timer = setTimeout(() => {
      const input = document.querySelector('input[inputmode="numeric"]');
      if (input) {
        input.focus();
      }
    }, 100);

    return () => clearTimeout(timer);
  }, [showConfirm]);

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/50 backdrop-blur-sm animate-fadeIn">
      <div className="bg-white rounded-2xl shadow-2xl p-8 w-full max-w-sm relative border border-gray-100 transform animate-scaleIn">
        <button
          onClick={onCancel}
          className="absolute top-4 right-4 text-gray-400 hover:text-gray-700 transition-colors duration-200"
          aria-label="Close"
        >
          <FaTimes className="text-xl" />
        </button>

        <div className="text-center mb-8">
          <div className="inline-flex items-center justify-center w-20 h-20 bg-blue-100 rounded-full mb-4">
            {isSetup ? (
              <FaShieldAlt className="text-4xl text-blue-600" />
            ) : (
              <FaLock className="text-4xl text-blue-600" />
            )}
          </div>
          <h2 className="text-2xl font-bold bg-gradient-to-r from-blue-600 to-indigo-600 bg-clip-text text-transparent">
            {isSetup
              ? showConfirm
                ? "Confirm your PIN"
                : "Set up a PIN"
              : "Enter your PIN"}
          </h2>
          <p className="text-gray-500 text-sm mt-2">
            {isSetup
              ? "Create a PIN to protect your passwords"
              : "Enter your PIN to view this password"}
          </p>
        </div>

        <form onSubmit={handleSubmit} className="space-y-5">
          <div className="flex justify-center">
            <input
              type="password"
              placeholder={showConfirm ? "Confirm PIN" : "Enter PIN"}
              value={showConfirm ? confirmPin : pin}
              onChange={showConfirm ? handleConfirmPinChange : handlePinChange}
              className="border border-gray-300 focus:border-blue-500 focus:ring-2 focus:ring-blue-200 p-4 w-full
              rounded-xl transition text-center text-2xl tracking-widest bg-gray-50 shadow-inner"
              autoFocus
              inputMode="numeric"
              pattern="[0-9]*"
              maxLength={6}
            />
          </div>

          {error && (
            <div className="text-red-500 text-center font-medium animate-fadeIn bg-red-50 p-3 rounded-xl">
              {error}
            </div>
          )}

          <button
            type="submit"
            className="bg-gradient-to-r from-blue-500 to-indigo-600 hover:from-blue-600 hover:to-indigo-700
            px-6 py-3 text-white rounded-xl flex items-center gap-2 font-semibold shadow-lg transition-all
            duration-300 w-full justify-center mt-4 hover:shadow-xl transform hover:scale-[1.02]"
          >
            {isSetup
              ? showConfirm
                ? <><FaCheck className="mr-1" /> Confirm PIN</>
                : "Continue"
              : <><FaLock className="mr-1" /> Unlock</>}
          </button>
        </form>
      </div>
    </div>
  );
}

import { useState, useEffect } from "react";
import { FaTimes, FaLock, FaShieldAlt, FaCheck, FaExclamationCircle } from "react-icons/fa";
import AuthService from "../services/AuthService";

export default function PinModal({ onSuccess, onCancel, isSetup = false }) {
  const [pin, setPin] = useState("");
  const [confirmPin, setConfirmPin] = useState("");
  const [error, setError] = useState("");
  const [showConfirm, setShowConfirm] = useState(false);

  const handlePinChange = (e) => {
    const value = e.target.value;
    // Only allow numbers
    if (/^\d*$/.test(value) && value.length <= 6) {
      setPin(value);
      setError("");
    }
  };

  const handleConfirmPinChange = (e) => {
    const value = e.target.value;
    if (/^\d*$/.test(value) && value.length <= 6) {
      setConfirmPin(value);
      setError("");
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (isSetup) {
      if (!showConfirm) {
        if (pin.length < 4) {
          setError("PIN must be at least 4 digits");
          return;
        }
        setShowConfirm(true);
        return;
      } else {
        if (pin !== confirmPin) {
          setError("PINs don't match");
          return;
        }

        AuthService.savePin(pin);
        onSuccess();
      }
    } else {
      // Verification mode
      if (AuthService.verifyPin(pin)) {
        onSuccess();
      } else {
        setError("Incorrect PIN");
      }
    }
  };

  // Focus the input when modal opens
  useEffect(() => {
    // Small delay to ensure DOM is ready
    const timer = setTimeout(() => {
      const input = document.querySelector('input[inputmode="numeric"]');
      if (input) {
        input.focus();
      }
    }, 100);

    return () => clearTimeout(timer);
  }, [showConfirm]);

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-gray-900/40 backdrop-blur-sm animate-fadeIn">
      <div className="bg-white rounded-2xl shadow-xl p-6 w-full max-w-sm mx-4 relative animate-scaleIn">
        <button
          onClick={onCancel}
          className="absolute top-4 right-4 text-gray-400 hover:text-gray-600 p-1.5 rounded-full hover:bg-gray-100 transition-colors"
          aria-label="Close"
        >
          <FaTimes/>
        </button>

        <div className="text-center my-6">
          <div className="w-16 h-16 rounded-full bg-indigo-100 text-indigo-600 flex items-center justify-center mx-auto mb-4">
            {isSetup ? <FaShieldAlt className="text-2xl"/> : <FaLock className="text-2xl"/>}
          </div>
          
          <h2 className="text-xl font-semibold text-gray-800 mb-1">
            {isSetup
                ? showConfirm
                  ? "Confirm your PIN"
                  : "Create a PIN"
                : "Enter your PIN"}
          </h2>
          
          <p className="text-gray-500 text-sm">
            {isSetup
                ? "Secure your passwords with a PIN code"
                : "Enter your PIN to continue"}
          </p>
        </div>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="flex justify-center">
            <input
              type="password"
              placeholder={showConfirm ? "Confirm PIN" : "Enter PIN"}
              value={showConfirm ? confirmPin : pin}
              onChange={showConfirm ? handleConfirmPinChange : handlePinChange}
              className="text-center text-2xl tracking-widest w-full px-4 py-3.5 rounded-xl border border-gray-200 
              focus:border-indigo-500 focus:ring-2 focus:ring-indigo-200 transition-all duration-200 bg-gray-50"
              autoFocus
              inputMode="numeric"
              pattern="[0-9]*"
              maxLength={6}
            />
          </div>

          {error && (
            <div className="flex items-center gap-2 text-red-600 bg-red-50 p-3 rounded-xl border border-red-100 animate-fadeIn">
              <FaExclamationCircle className="flex-shrink-0"/>
              <span className="text-sm">{error}</span>
            </div>
          )}

          <button
            type="submit"
            className="w-full bg-indigo-600 hover:bg-indigo-700 text-white py-3 px-4 rounded-xl 
            flex items-center justify-center gap-2 transition-all duration-300 shadow-md hover:shadow-lg"
          >
            {isSetup
                ? showConfirm
                  ? <><FaCheck className="mr-1"/> Confirm PIN</>
                  : "Continue"
                : <><FaLock className="mr-1"/> Unlock</>}
          </button>
          
          {!isSetup && (
            <button
              type="button"
              onClick={onCancel}
              className="w-full py-2.5 px-4 bg-gray-100 hover:bg-gray-200 text-gray-800 rounded-xl 
              transition-colors duration-200 mt-2"
            >
              Cancel
            </button>
          )}
        </form>
      </div>
    </div>
  );
}

import {FaClipboard, FaTimes, FaCheck} from "react-icons/fa";
import {useState} from "react";

export default function ShowPasswordModal({viewPassword, closeViewModal}) {
    const [copied, setCopied] = useState(false);

    async function handleCopyPassword(password) {
        if (password) {
            try {
                await navigator.clipboard.writeText(password);
                setCopied(true);
                setTimeout(() => setCopied(false), 2000);
            } catch {
                setCopied(false);
            }
        }
    }

    return (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/50 backdrop-blur-sm animate-fadeIn">
            <div className="bg-white rounded-2xl shadow-2xl p-8 w-full max-w-sm relative border border-gray-100 transform animate-scaleIn">
                <button
                    onClick={() => closeViewModal() && setCopied(false)}
                    className="absolute top-4 right-4 text-gray-400 hover:text-gray-700 transition-colors duration-200"
                    aria-label="Close"
                >
                    <FaTimes className="text-xl"/>
                </button>
                <h2 className="text-2xl font-bold mb-6 bg-gradient-to-r from-blue-600 to-indigo-600 bg-clip-text text-transparent">
                    Password Details
                </h2>
                <div className="space-y-5">
                    <div className="bg-gray-50 p-4 rounded-xl border border-gray-100">
                        <span className="font-semibold text-gray-700 block mb-1">Title</span>
                        <span className="text-gray-800">{viewPassword.title}</span>
                    </div>
                    <div className="bg-gray-50 p-4 rounded-xl border border-gray-100">
                        <span className="font-semibold text-gray-700 block mb-1">Username</span>
                        <span className="text-gray-800">{viewPassword.username}</span>
                    </div>
                    <div className="bg-gray-50 p-4 rounded-xl border border-gray-100">
                        <div className="flex items-center justify-between mb-1">
                            <span className="font-semibold text-gray-700">Password</span>
                            <button
                                className={`p-2 rounded-lg ${copied ? 'bg-green-100 text-green-700' : 'bg-blue-100 text-blue-700'} 
                                transition-all duration-300 hover:shadow-md transform hover:scale-105`}
                                aria-label="Copy password"
                                onClick={() => handleCopyPassword(viewPassword.password)}
                            >
                                {copied ? <FaCheck className="text-green-600"/> : <FaClipboard/>}
                            </button>
                        </div>
                        <div className="flex items-center">
                            <span className="font-mono tracking-wider text-gray-800 bg-gray-100 p-2 rounded-lg w-full break-all">
                                {viewPassword.password}
                            </span>
                        </div>
                        {copied && (
                            <span className="text-green-600 font-medium text-sm mt-2 block animate-fadeIn">
                                Copied to clipboard!
                            </span>
                        )}
                    </div>
                </div>
            </div>
        </div>
    )
}

import {FaClipboard, FaTimes, FaCheck, FaKey, FaUser, FaLock} from "react-icons/fa";
import {useState} from "react";

export default function ShowPasswordModal({viewPassword, closeViewModal}) {
    const [copied, setCopied] = useState(false);

    async function handleCopyPassword(password) {
        if (password) {
            try {
                await navigator.clipboard.writeText(password);
                setCopied(true);
                setTimeout(() => setCopied(false), 2000);
            } catch {
                setCopied(false);
            }
        }
    }

    return (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-gray-900/40 backdrop-blur-sm animate-fadeIn">
            <div className="bg-white rounded-2xl shadow-xl p-6 w-full max-w-md mx-4 relative animate-scaleIn">
                <button
                    onClick={() => closeViewModal() && setCopied(false)}
                    className="absolute top-4 right-4 text-gray-400 hover:text-gray-600 p-1.5 rounded-full hover:bg-gray-100 transition-colors"
                    aria-label="Close"
                >
                    <FaTimes/>
                </button>
                
                <div className="flex items-center gap-3 mb-6">
                    <div className="bg-indigo-100 p-2.5 rounded-lg text-indigo-600">
                        <FaKey className="text-xl"/>
                    </div>
                    <h2 className="text-xl font-semibold text-gray-800">
                        {viewPassword.title}
                    </h2>
                </div>
                
                <div className="space-y-4">
                    <div className="flex items-center gap-3 p-4 bg-gray-50 rounded-xl">
                        <div className="text-indigo-500">
                            <FaUser/>
                        </div>
                        <div>
                            <div className="text-sm text-gray-500 mb-0.5">Username</div>
                            <div className="text-gray-800 font-medium">{viewPassword.username}</div>
                        </div>
                    </div>
                    
                    <div className="p-4 bg-gray-50 rounded-xl">
                        <div className="flex items-center justify-between mb-2">
                            <div className="flex items-center gap-3">
                                <div className="text-indigo-500">
                                    <FaLock/>
                                </div>
                                <div className="text-sm text-gray-500">Password</div>
                            </div>
                            <button
                                className={`p-2 rounded-lg transition-all duration-200 ${
                                    copied 
                                        ? 'bg-green-100 text-green-700' 
                                        : 'bg-gray-100 text-gray-700 hover:bg-indigo-100 hover:text-indigo-700'
                                }`}
                                onClick={() => handleCopyPassword(viewPassword.password)}
                                aria-label="Copy password"
                            >
                                {copied ? <FaCheck/> : <FaClipboard/>}
                            </button>
                        </div>
                        
                        <div className="mt-1 font-mono tracking-wider text-gray-800 bg-white p-3 rounded-lg border border-gray-200 break-all">
                            {viewPassword.password}
                        </div>
                        
                        {copied && (
                            <div className="mt-2 text-green-600 text-sm font-medium flex items-center gap-1.5 animate-fadeIn">
                                <FaCheck size={12}/> Copied to clipboard
                            </div>
                        )}
                    </div>
                    
                    <button
                        onClick={() => closeViewModal() && setCopied(false)}
                        className="w-full py-2.5 px-4 bg-gray-100 hover:bg-gray-200 text-gray-800 rounded-xl 
                        transition-colors duration-200 mt-2"
                    >
                        Close
                    </button>
                </div>
            </div>
        </div>
    );
}

"use client";

import { createContext, useState, useEffect, useContext } from 'react';

const ThemeContext = createContext({
  theme: 'light',
  toggleTheme: () => {},
});

export function ThemeProvider({ children }) {
  const [theme, setTheme] = useState('light');

  // Initialize theme from localStorage (or default to light)
  useEffect(() => {
    const savedTheme = localStorage.getItem('theme');
    const prefersDark = window.matchMedia('(prefers-color-scheme: dark)').matches;

    if (savedTheme) {
      setTheme(savedTheme);
      document.documentElement.classList.toggle('dark', savedTheme === 'dark');
    } else if (prefersDark) {
      setTheme('dark');
      document.documentElement.classList.add('dark');
    }
  }, []);

  const toggleTheme = () => {
    const newTheme = theme === 'light' ? 'dark' : 'light';
    setTheme(newTheme);
    localStorage.setItem('theme', newTheme);
    document.documentElement.classList.toggle('dark', newTheme === 'dark');
  };

  return (
    <ThemeContext.Provider value={{ theme, toggleTheme }}>
      {children}
    </ThemeContext.Provider>
  );
}

export function useTheme() {
  return useContext(ThemeContext);
}

